choose.when:
SELECT COUNT(*)
FROM TB_TEAM
WHERE ID = #{reserverId}

SELECT COUNT(*)
FROM TB_USER
WHERE ID = #{reserverId}

mapper.insert:

    SELECT ID
    FROM (
        SELECT ID
        FROM TB_RESERVER
        ORDER BY ID DESC
    )
    WHERE ROWNUM = 1

INSERT INTO TB_RESERVER (
    RESERVER_TYPE
) VALUES (
    #{reserverType}
)

INSERT INTO TB_USER_RESERVER (
    RESERVER_ID,
    USER_ID
) VALUES (
    #{reserverId},
    #{userId}
)

INSERT INTO TB_TEAM_RESERVER (
    RESERVER_ID,
    TEAM_ID
) VALUES (
    #{reserverId},
    #{teamId}
)
